INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40401, 16406, 'Cisatracurium', 'Respiratory Insufficiency', 'Neuromuscular blocking agents can cause respiratory depression and paralysis.  Therapy with neuromuscular blocking agents should be administered cautiously in patients with pulmonary impairment.  Treatment of respiratory paralysis consists of positive-pressure artificial respiration with oxygen and maintenance of a patent airway until the recovery of normal respiration is assured.', '3', '', 'DDInter', 0);
