INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40753, 11206, 'Clemastine', 'Anticholinergic Syndrome', 'Antihistamines often have anticholinergic activity, to which elderly patients are particularly sensitive.  Therapy with antihistamines should be administered cautiously, if at all, in patients with preexisting conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention or obstruction; angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma; and gastrointestinal obstructive disorders.  Conventional, first-generation antihistamines such as the ethanolamines (bromodiphenhydramine, carbinoxamine, clemastine, dimenhydrinate, diphenhydramine, doxylamine, phenyltoloxamine) tend to exhibit substantial anticholinergic effects.  In contrast, the newer, relatively nonsedating antihistamines (e.g., cetirizine, fexofenadine, loratadine) reportedly have low to minimal anticholinergic activity at normally recommended dosages and may be appropriate alternatives.', '2', '', 'DDInter', 0);
