INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40856, 2758, 'Clindamycin', 'Liver Diseases', 'Clindamycin is primarily metabolized by the liver.  The serum concentration of clindamycin may be increased and the half-life prolonged in patients with severely impaired hepatic function.  In addition, jaundice and liver enzyme abnormalities may occur during use of the drug.  Therapy with clindamycin should be administered cautiously in patients with liver disease.  Dosage adjustments may not be necessary when administered every 8 hours.  However, serum concentrations should be monitored during high-dose therapy, and periodic liver function tests should be performed during prolonged therapy.', '2', '', 'DDInter', 0);
