INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41024, 720, 'Clomipramine', 'Bone Marrow Failure Disorders', 'The use of tricyclic and tetracyclic antidepressants (TCAs) has rarely been associated with bone marrow suppression.  Leukopenia, agranulocytosis, thrombocytopenia, anemia, eosinophilia, purpura, and pancytopenia have been reported with some TCAs.  Patients with preexisting bone marrow suppression or blood dyscrasias receiving TCAs should be monitored closely during therapy for further decreases in blood counts.', '2', '', 'DDInter', 0);
