INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41032, 720, 'Clomipramine', 'Diabetes Mellitus', 'Both elevation and lowering of blood sugar levels have been reported with the use of some tricyclic antidepressants (TCAs).  Rarely, these effects have also occurred with maprotiline, a tetracyclic antidepressant.  Patients with diabetes should be monitored for worsening control of blood glucose when treated with these agents, particularly during dosage escalation or whenever dosage has been altered.', '2', '', 'DDInter', 0);
