INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41112, 0, 'Alemtuzumab', 'Pancytopenia', 'Severe, including fatal, autoimmune anemia and thrombocytopenia, and prolonged myelosuppression have been reported in patients receiving alemtuzumab.  It is recommended to withhold therapy for severe cytopenias and to discontinue therapy for autoimmune cytopenias or recurrent/persistent severe cytopenias (except lymphopenia).  It is recommended to monitor complete blood counts (CBC) at weekly intervals during therapy and more frequently if worsening anemia, neutropenia, or thrombocytopenia occurs and to assess CD4+ counts after treatment until recovery to >= 200 cells/µL.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41113, 0, 'Alemtuzumab', 'Heart Diseases', 'The use of alemtuzumab can cause severe infusion reactions, including pyrexia, chills/rigors, nausea, hypotension, urticaria, dyspnea, rash, emesis, and bronchospasm.  Caution should be taken in patients with a history of pulmonary disease, heart disease as they may require dose-escalation and additional pre-infusion medications to manage complications as appropriate.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41114, 0, 'Alemtuzumab', 'Infections', 'Alemtuzumab should be used with caution in patients at risk of opportunistic infections, as therapy could result in severe and prolonged lymphopenia.  It is recommended to administer prophylaxis against Pneumocystis jiroveci pneumonia (PCP) and herpes viral during and for a minimum of 2 months after completion of therapy or until the CD4+ count is >=200 cells/µL.  Routinely monitor patients for CMV infection during therapy and withhold therapy for serious infections and during antiviral treatment for CMV infection or confirmed CMV viremia.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41115, 4293, 'Clidinium', 'Arrhythmias, Cardiac', 'Patients with tachycardia should be supervised closely during treatment with anticholinergic agents.  Tachycardia is produced by blocking normal vagal inhibition of the SA node.  Paradoxically, bradycardia may occur due to central vagal stimulation which may occur prior to peripheral cholinergic blockade.', '3', '', 'DDInter', 0);
