INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41140, 4293, 'Clidinium', 'Tardive Dyskinesia', 'Anticholinergic agents and agents with secondary anticholinergic activity may aggravate tardive dyskinesia or induce previously suppressed symptoms.  Therapy with these agents should be avoided, if possible, or administered cautiously in patients with preexisting tardive dyskinesia, particularly in the elderly.  If tardive dyskinesia symptoms develop or worsen during treatment with an anticholinergic agent, prompt withdrawal of therapy will provide better chances of improving the condition.', '3', '', 'DDInter', 0);
