INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41165, 15942, 'Clonidine', 'Bradycardia', 'Central alpha-2 adrenoreceptor agonists reduce sympathetic outflow from the central nervous system.  Heart rate is decreased, which may lead to or exacerbate sinus bradycardia and atrioventricular block.  Therapy with central alpha-2 adrenoreceptor agonists should be administered cautiously in patients with conduction disturbances such as sinus node dysfunction or AV nodal disease.', '2', '', 'DDInter', 0);
