INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41169, 15942, 'Clonidine', 'Hypotension', 'Central alpha-2 adrenoreceptor agonists reduce sympathetic outflow from the central nervous system, resulting in decreases in heart rate, peripheral and renovascular resistance, and blood pressure.  Therapy with these agents should be administered cautiously in patients with hypotension or conditions that may be exacerbated by decreased blood pressure and perfusion, such as coronary insufficiency, peripheral vascular disease (e.g., Raynaud''s syndrome), cerebrovascular disease, or recent myocardial infarction.', '2', '', 'DDInter', 0);
