INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41171, 15942, 'Clonidine', 'Kidney Diseases', 'Clonidine is primarily eliminated unchanged by the kidney.  The serum concentrations and half-life of clonidine may be increased in patients with impaired renal function, so patients may benefit from a lower initial dose.  Dosage adjustments may be necessary and modifications should be based on the degree of renal impairment.  Only a minimal amount of clonidine is removed during routine hemodialysis, and there is no need to give supplemental clonidine following dialysis.', '2', '', 'DDInter', 0);
