INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41459, 2822, 'Clozapine', 'Anticholinergic Syndrome', 'Neuroleptic agents have anticholinergic activity, to which elderly patients are particularly sensitive.  Clozapine and low-potency agents such as chlorpromazine and thioridazine tend to exhibit greater anticholinergic effects than other agents in the class.  Clozapine use has been associated with impairment of intestinal peristalsis ranging from constipation to intestinal obstruction, fecal impaction, and paralytic ileus.  Therapy with neuroleptic agents should be administered cautiously in patients with preexisting conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention or obstruction; angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma; and gastrointestinal obstructive disorders.', '3', '', 'DDInter', 0);
