INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41601, 29811, 'Clozapine', 'Weight Gain', 'Weight gain has been observed with atypical antipsychotic use.  While all agents in the class have been shown to produce some changes, each drug has its own specific risk profile.  When treating pediatric patients with atypical antipsychotic agents, weight gain should be monitored and assessed against that expected for normal growth.  Monitor weight at baseline and frequently thereafter.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41602, 2822, 'Clozapine', 'Kidney Diseases', 'Clozapine is extensively metabolized by the liver and subsequently excreted in the urine (50%) and feces (35%), primarily as metabolites.  One of the metabolites appears to have only limited pharmacologic activity, while the others are inactive.  However, the effects of possible metabolite accumulation have not been studied.  The manufacturer recommends that therapy with clozapine be administered cautiously in patients with impaired renal function.', '2', '', 'DDInter', 0);
