INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41784, 14117, 'Colesevelam', 'Hyperlipoproteinemias', 'The manufacturer considers the use of colesevelam to be contraindicated in patients with TG levels >500 mg/dL.  Colesevelam, like other bile acid sequestrants, can increase serum TG concentrations.  Lipid parameters, including TG levels and non-HDL-C, should be obtained before starting colesevelam and periodically thereafter.  Caution should be exercised when treating patients with TG levels greater than 300 mg/dL.  Colesevelam should be discontinued if TG levels exceed 500 mg/dL or if the patient develops hypertriglyceridemia-induced pancreatitis.', '3', '', 'DDInter', 0);
