INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41901, 16989, 'Cyclobenzaprine', 'Anticholinergic Syndrome', 'Cyclobenzaprine has anticholinergic activity, to which elderly patients are particularly sensitive.  Therapy with cyclobenzaprine should be administered cautiously in patients with preexisting conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention or obstruction; angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma; and gastrointestinal obstructive disorders.  Glaucoma should be treated and under control prior to initiation of cyclobenzaprine therapy, and intraocular pressure monitored during therapy.', '2', '', 'DDInter', 0);
