INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41917, 3243, 'Cyproheptadine', 'Liver Diseases', 'Limited pharmacokinetic data are available for the older, first-generation antihistamines.  Many appear to be primarily metabolized by the liver, and both parent drugs and metabolites are excreted in the urine.  Patients with renal and/or liver disease may be at greater risk for adverse effects from antihistamines due to drug and metabolite accumulation.  Therapy with antihistamines should be administered cautiously in such patients.  Lower initial dosages may be appropriate.', '2', '', 'DDInter', 0);
