INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41947, 4177, 'Cyclophosphamide', 'Lung Diseases', 'Pneumonitis, pulmonary fibrosis, pulmonary veno-occlusive disease and other forms of pulmonary toxicity leading to respiratory failure have been reported during and following treatment with cyclophosphamide.  Pneumonitis may develop years after treatment with cyclophosphamide.  Patients should be monitored for signs and symptoms of pulmonary toxicity.  Caution is advised in patients with preexisting pulmonary impairment.', '2', '', 'DDInter', 0);
