INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41967, 1235, 'Cyanocobalamin', 'Malabsorption Syndromes', 'Absorption of vitamin B12 is decreased in malabsorption syndromes and in pernicious anemia without coadministration of intrinsic factor (IF).  Cyanocobalamin is rapidly absorbed following IM or subcutaneous injections.  Hydroxocobalamin (cyanocobalamin analog) functions the same a cyanocobalamin, but has induced antibody formation to the hydroxocobalamin-transcobalamin II complex.  The use of cyanocobalamin may be preferred in patients with these conditions.', '2', '', 'DDInter', 0);
