INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41971, 1235, 'Cyanocobalamin', 'Kidney Diseases', 'Cyanocobalamin products contain aluminum that may be toxic and may reach toxic levels with prolonged parenteral administration if kidney function is impaired.  Premature neonates are particularly at risk because their kidneys are immature, and they require large amounts of calcium and other solutions which also contain aluminum.  Research indicates that patients with impaired kidney function, including premature neonates, who receive parenteral levels of aluminum greater than 4-5 mcg/kg/day accumulate aluminum at levels associated with central nervous system and bone toxicity.  Caution and monitoring are recommended.', '2', '', 'DDInter', 0);
