INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41975, 14350, 'Cytarabine', 'Infections', 'Because of their cytotoxic effects on rapidly proliferating tissues, antineoplastic agents frequently can, to varying extent, induce myelosuppression.  The use of these drugs may be contraindicated in patients with known infectious diseases.  All patients should be instructed to immediately report any signs or symptoms suggesting infection such as fever, sore throat, or local infection during antineoplastic therapy.  Close clinical monitoring of hematopoietic function is recommended.', '3', '', 'DDInter', 0);
