INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42096, 963, 'Cyclosporine', 'Malabsorption Syndromes', 'Patients with malabsorption syndromes may have difficulty achieving therapeutic serum concentrations with the Sandimmune formulations (capsules or solution) of cyclosporine.  An alternative formulation, Neoral (capsule or solution for microemulsion), has increased bioavailability compared to Sandimmune.  Sandimmune and Neoral are not bioequivalent and should not be used interchangeably without physician supervision.', '2', '', 'DDInter', 0);
