INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42258, 14351, 'Darbepoetin alfa', 'Thromboembolism', 'The use of erythropoiesis-stimulating agents may be associated with an increased risk of thrombotic events and mortality.  During clinical trials, an increased risk was observed in:  1) patients with chronic renal failure (CRF) and ischemic heart disease or congestive heart failure targeted to a maintenance hematocrit of 42%, and 2) in patients without CRF undergoing coronary artery bypass surgery.  Although a causal relationship has not been established, therapy with these agents should be administered cautiously in patients with preexisting cardiovascular disease and/or a history of thrombotic events.', '3', '', 'DDInter', 0);
