INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42282, 19378, 'Dalteparin', 'Kidney Diseases', 'Systemic exposure and the risk of bleeding may be increased when heparin is used in the presence of severe renal impairment.  Therapy with heparin should be administered cautiously in patients with significantly impaired renal function.  A reduction in dosage may be necessary.', '3', '', 'DDInter', 0);
