INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42288, 4352, 'Darolutamide', 'Hepatic Insufficiency', 'Patients with moderate hepatic impairment (Child-Pugh Class B) have a higher exposure to darolutamide and reduction of the dose is recommended (see manufacturer package insert for recommended dose).  No dose reduction is needed for patients with mild hepatic impairment.  The effect of severe hepatic impairment (Child-Pugh C) on darolutamide pharmacokinetics is unknown.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42289, 4352, 'Darolutamide', 'Kidney Diseases', 'Patients with severe renal impairment (eGFR 15-29 mL/min/1.73 m2) who are not receiving hemodialysis have a higher exposure to darolutamide and reduction of the dose is recommended (see manufacturer package insert for recommended dose).  No dose reduction is needed for patients with mild or moderate renal impairment.  The effect of end stage renal disease (eGFR <15 mL/min/1.73 m2) on darolutamide pharmacokinetics is unknown.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42290, 3326, 'Dapsone', 'Bone Marrow Failure Disorders', 'The use of dapsone has been associated with development of agranulocytosis, aplastic anemia, and other blood dyscrasias.  Blood dyscrasias are more likely to occur in patients with glucose-6-phosphate dehydrogenase (G-6-PD) deficiency.  Patients should be instructed to report any signs of infection (sore throat, fever, local infection, bleeding, or symptoms of anemia).  Monitoring for hematologic abnormalities is recommended.', '3', '', 'DDInter', 0);
