INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42401, 27431, 'Dasatinib', 'Long QT Syndrome', 'Dasatinib has shown to prolong QT interval in a concentration-dependent manner.  Caution should be exercised specially in patients with congenital long QT syndrome, patients with hypokalemia, hypomagnesemia, or with history of arrhythmias and on antiarrhythmic medication.  Hypokalemia or hypomagnesemia should be corrected before starting treatment.  Patients at risk should be monitored with an EKG before treatment and periodically as clinically indicated.  Dose adjustments might be needed.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42402, 9026, 'Allopurinol', 'Bone Marrow Failure Disorders', 'Bone marrow suppression has been reported in patients receiving allopurinol; however, most of these patients were receiving concomitant medications with the known potential to cause such an effect.  The suppression has occurred from as early as 6 weeks to as long as 6 years after the initiation of allopurinol therapy.  Therapy with allopurinol should be administered cautiously to patients with or predisposed to bone marrow suppression.', '2', '', 'DDInter', 0);
