INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42413, 9026, 'Allopurinol', 'Dehydration', 'Adequate hydration is necessary during therapy with allopurinol to prevent both the formation of xanthine calculi and renal precipitation of urates when concomitant uricosuric agents are given.  Patients who are dehydrated (e.g., due to severe diarrhea or vomiting) may be at increased risk and should be encouraged to consume additional amounts of liquid or given intravenous fluid.  In general, fluid intake sufficient to yield a daily urinary output of at least 2 liters is recommended.  Maintenance of a slightly alkaline or neutral urine is also desirable.', '2', '', 'DDInter', 0);
