INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42424, 9026, 'Allopurinol', 'Liver Diseases', 'The use of allopurinol has infrequently been associated with asymptomatic elevations of serum transaminase and alkaline phosphatase levels.  Isolated cases of cholestatic jaundice, hepatic necrosis, granulomatous hepatitis, and hepatomegaly have also been reported.  Therapy with allopurinol should be administered cautiously in patients with liver disease.  Periodic monitoring of liver function tests is recommended in these patients during the early stages of therapy.', '2', '', 'DDInter', 0);
