INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42435, 9026, 'Allopurinol', 'Kidney Diseases', 'Patients with decreased renal function require lower dosages of allopurinol than normal to control serum urate levels.  Therapy should be initiated at reduced dosages with smaller incremental changes in such patients.  Additionally, individuals with preexisting renal disease or poor urate clearance have demonstrated a rise in BUN during therapy with allopurinol.  Renal failure has also been observed among some with hyperuricemia secondary to neoplastic diseases.  Renal function should be monitored during the initial stages of therapy, and the dosage reduced or the drug withdrawn if necessary.', '2', '', 'DDInter', 0);
