INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42454, 17122, 'Daunorubicin', 'Kidney Diseases', 'Approximately 25% of daunorubicin is eliminated in an active form in the urine.  Patient with renal impairment are at risk of greater toxicity noted at the recommended dose of daunorubicin.  Therapy with daunorubicin should be administered cautiously and at a reduced dosage in patients with significantly compromised renal function.  Clinical evaluation of renal function prior to each course of treatment is recommended.', '2', '', 'DDInter', 0);
