INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42468, 3511, 'Deferoxamine', 'Toxic Optic Neuropathy', 'Deferoxamine may cause blurred vision, cataracts, decreased visual acuity, and impaired color, peripheral, and night vision.  Ocular toxicities have occurred after prolonged therapy, large doses, and in patients with decreased ferritin levels.  Therapy with deferoxamine should be administered cautiously in patients with or predisposed to visual defects.  Periodic examination of visual acuity is recommended in patients receiving long term deferoxamine therapy.  Ocular dysfunction is usually reversible if detected early and deferoxamine is withdrawn promptly.', '2', '', 'DDInter', 0);
