INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42470, 3511, 'Deferoxamine', 'Respiratory Distress Syndrome', 'Acute respiratory distress syndrome has been described in patients with acute iron intoxication or thalassemia following treatment with excessively high intravenous doses of deferoxamine.  Caution is recommended when using deferoxamine in these patients.', '2', '', 'DDInter', 0);
