INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42519, 14130, 'Deferasirox', 'Auditory Diseases, Central', 'Auditory disturbances (high-frequency hearing loss, decreased hearing), and ocular disturbances (lens opacities, cataracts, elevations in intraocular pressure, and retinal disorders) have been reported in patients using deferasirox.  It is recommended to perform auditory and ophthalmic testing (including slit lamp examinations and dilated fundoscopy) before starting therapy with deferasirox and thereafter as clinically indicated.  If disturbances are noted, consider dose reduction or interruption if appropriate.', '2', '', 'DDInter', 0);
