INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42541, 4818, 'Deferiprone', 'Agranulocytosis', 'Fatal agranulocytosis and neutropenia can occur with the use of deferiprone.  It is recommended to obtain an absolute neutrophil count before initiating treatment with deferiprone and monitor weekly thereafter or as clinically appropriate.  Manage agranulocytosis/neutropenia prior to initiating treatment.  Therapy should be interrupted if neutropenia develops or if an infection develops.  Do not resume therapy in patients who have developed agranulocytosis and do not rechallenge patients who develop neutropenia unless potential benefits outweigh potential risks.', '3', '', 'DDInter', 0);
