INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42636, 24245, 'Abatacept', 'Hepatitis B', 'Because antirheumatic therapies have been associated with hepatitis B reactivation, patients should be screened for viral hepatitis in accordance with published guidelines before starting therapy with abatacept.  Patients screening positive for hepatitis were excluded from clinical studies with abatacept.', '2', '', 'DDInter', 0);
