INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42725, 2971, 'Dexmethylphenidate', 'Mental Disorders', 'The use of central nervous system (CNS) stimulants is contraindicated in patients with marked agitation and/or anxiety, since these symptoms may be aggravated.  CNS stimulants may also exacerbate symptoms of behavior disturbance and thought disorder in psychotic patients, particularly children.  Therapy with CNS stimulants should be administered cautiously in patients with a history of psychosis or a predisposition to agitated states.', '3', '', 'DDInter', 0);
