INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42737, 2971, 'Dexmethylphenidate', 'Liver Diseases', 'In general, CNS stimulants are extensively metabolized by the liver.  Their plasma clearance may be decreased and their half-life prolonged in patients with impaired hepatic function.  Therapy with CNS stimulants should be administered cautiously in patients with moderate to severe liver disease, and the dosage should be adjusted accordingly.  Additionally, postmarketing reports have shown that atomoxetine can cause severe liver injury.  Laboratory testing should be done at the first sign or symptom of liver dysfunction (jaundice, dark urine, upper quadrant tenderness) and treatment should be discontinued in patients with evidence of liver injury.', '3', '', 'DDInter', 0);
