INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42758, 2971, 'Dexmethylphenidate', 'Hematologic Diseases', 'Hematologic toxicity, including thrombocytopenia, easy bruisability, epistaxis, leukopenia, anemia and eosinophilia, has been reported rarely during use of methylphenidate.  However, a causal relationship has not been established.  Therapy with methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) should be administered cautiously in patients with preexisting blood dyscrasias or bone marrow suppression.  Periodic hematologic monitoring may be appropriate in all patients during prolonged therapy.', '2', '', 'DDInter', 0);
