INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42797, 6700, 'Desmopressin', 'Vascular Diseases', 'The use of desmopressin, primarily in parenteral administration and/or in large doses, has been infrequently associated with changes in blood pressure, causing either slight elevations or transient decreases with a compensatory increase in heart rate.  Therapy with desmopressin, regardless of route of administration, should be administered cautiously in patients with coronary artery insufficiency and/or hypertensive cardiovascular disease.', '2', '', 'DDInter', 0);
