INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (43589, 3397, 'Dexamethasone', 'Strongyloidiasis', 'Unlike most helminths, Strongyloides stercoralis has the ability to replicate in the human host.  In patients with strongyloidiasis, the use of pharmacologic or immunosuppressive dosages of corticosteroids may result in Strongyloides hyperinfection and dissemination with widespread larval migration, often accompanied by severe enterocolitis and potentially fatal gram-negative septicemia.  Therapy with corticosteroids should be administered with extreme caution, if at all, in these patients.  For patients on corticosteroids who develop known or suspected Strongyloides infestation, withdrawal of corticosteroids or reduction of the dose of corticosteroids is recommended.', '2', '', 'DDInter', 0);
