INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (43779, 8738, 'Diazepam', 'Liver Diseases', 'Benzodiazepines are metabolized by the liver, and the metabolites are excreted in the urine.  Chlordiazepoxide, clorazepate, diazepam, flurazepam and quazepam undergo oxidative N-dealkylation to active metabolites that are substantially longer-acting than the parent compound.  These metabolites then undergo further biotransformation to pharmacologically inactive products before excretion by the kidney.  Therapy with benzodiazepines should be administered cautiously at lower initial dosages in patients with impaired renal and/or hepatic function.  Agents that are converted to weakly active, short-acting, or inactive metabolites may be preferable in hepatic impairment.  Lorazepam, oxazepam and temazepam are conjugated to inactive metabolites, while alprazolam, estazolam and triazolam undergo hydroxylation to weakly active or inactive metabolites.', '3', '', 'DDInter', 0);
