INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (43856, 8738, 'Diazepam', 'Psychotic Disorders', 'Paradoxical reactions, including excitability, irritability, aggressive behavior, agitation, nervousness, hostility, anxiety, sleep disturbances, nightmares and vivid dreams, have been reported with the use of benzodiazepines in psychiatric patients and pediatric patients with hyperactive aggressive disorders.  Such patients should be monitored for signs of paradoxical stimulation during therapy with benzodiazepines.  The manufacturers do not recommend the use of benzodiazepines for the treatment of psychosis.', '2', '', 'DDInter', 0);
