INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (46101, 30822, 'Diclofenac', 'Porphyrias', 'There has been an isolated report associating the use of diclofenac with precipitation of a porphyria attack.  The proposed mechanism, demonstrated in rats, is through stimulation by diclofenac (and possibly other nonsteroidal anti-inflammatory agents) of the porphyrin precursor, delta-aminolevulinic acid.  The manufacturer recommends avoiding diclofenac in patients with hepatic porphyria.', '2', '', 'DDInter', 0);
