INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47249, 443, 'Alprazolam', 'Obesity', 'The plasma half-lives of benzodiazepines may be prolonged in obese patients, presumably due to increased distribution into fat.  Marked increases in distribution (> 100%) have been reported for diazepam and midazolam, and moderate increases (25% to 100%) for alprazolam, lorazepam, and oxazepam.  Therapy with benzodiazepines should be administered cautiously in obese patients, with careful monitoring of CNS status.  Longer dosing intervals may be appropriate.  When dosing by weight, loading doses should be based on actual body weight, while maintenance dose should be based on ideal body weight to avoid toxicity.', '2', '', 'DDInter', 0);
