INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47303, 3789, 'Diflunisal', 'Water-Electrolyte Imbalance', 'Fluid retention and edema have been reported in association with the use of nonsteroidal anti-inflammatory drugs (NSAIDs).  Therapy with NSAIDs should be administered cautiously in patients with preexisting fluid retention, hypertension, or a history of heart failure.  Blood pressure and cardiovascular status should be monitored closely during the initiation of NSAID treatment and throughout the course of therapy.', '3', '', 'DDInter', 0);
