INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47487, 5824, 'Dimethyl fumarate', 'Leukoencephalopathy, Progressive Multifocal', 'Progressive multifocal leukoencephalopathy (PML) has occurred in patients with multiple sclerosis (MS) treated with dimethyl fumarate; PML is an opportunistic viral infection of the brain caused by the John Cunningham virus (JC virus) that generally only occurs in immunocompromised patients and usually leads to death or severe disability.  Caution and monitoring are advised in patients with MS.  Dimethyl fumarate should be withheld at the first sign/symptom suggestive of PML and an appropriate diagnostic evaluation should be performed.', '2', '', 'DDInter', 0);
