INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47603, 449, 'Diltiazem', 'Wolff-Parkinson-White Syndrome', 'The use of intravenous diltiazem is contraindicated for the management of atrial flutter or fibrillation in patients with an accessory AV tract (e.g., those with Wolff-Parkinson-White or Lown-Ganong-Levine syndrome).  Diltiazem can cause ventricular fibrillation and cardiac arrest in such patients, the mechanism of which is related to the drug''s ability to shorten the refractory period and accelerate antegrade conduction within the accessory pathway.', '3', '', 'DDInter', 0);
