INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47637, 449, 'Diltiazem', 'Kidney Diseases', 'Diltiazem is extensively metabolized by the liver and subsequently excreted in the urine, primarily as metabolites.  Limited data suggest that the pharmacokinetic disposition of diltiazem is not altered in the presence of renal insufficiency or even end-stage renal disease.  However, the effects of possible metabolite accumulation have not been studied.  The manufacturers recommend that therapy with diltiazem be administered cautiously in patients with impaired renal function.  Laboratory parameters of renal function should be monitored at regular intervals.', '2', '', 'DDInter', 0);
