INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47754, 11214, 'Docetaxel', 'Edema', 'Severe fluid retention characterized by peripheral edema, generalized edema, pleural effusion, dyspnea at rest, cardiac tamponade, or abdominal distention due to ascites has been reported during docetaxel therapy.   Therapy with docetaxel should be administered cautiously to patients adversely affected by fluid retention.  Prophylaxis with a five day dexamethasone premedication regimen is recommended, however, fluid retention has occurred despite this pretreatment.', '3', '', 'DDInter', 0);
