INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47882, 6907, 'Doxorubicin', 'Bone Marrow Failure Disorders', 'Doxorubicin induces dose-related myelosuppression, primarily affecting leukocytes.  Therapy with doxorubicin should be withheld in patients whose bone marrow function is severely depressed by prior irradiation or chemotherapy or whose marrow function is recovering from previous cytotoxic therapy.  If the need outweighs the risk, extreme caution should be exercised in administering doxorubicin and the dosage should be reduced.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitory of hematopoietic function is recommended.  Doxorubicin is contraindicated in patients with severe persistent drug-induced myelosuppression', '3', '', 'DDInter', 0);
