INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47901, 3827, 'Donepezil', 'Bradycardia', 'Due to their pharmacological action, cholinesterase inhibitors can have a vagotonic effect on the sinoatrial and atrioventricular nodes producing bradycardia or heart block.  Therapy with cholinesterase inhibitors should be administered cautiously in patients with preexisting bradycardia or underlying cardiac conduction abnormalities.  Syncopal episodes have been reported in patients with and without cardiac abnormalities.  Atropine may be used to reverse bradycardia produced by cholinesterase inhibitors.', '3', '', 'DDInter', 0);
