INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48097, 6921, 'Dorzolamide (ophthalmic)', 'Porphyrias', 'Sulfonamides may be systemically absorbed when applied to the skin, eye, or mucosal membranes.  Therapy with topical sulfonamides should be administered cautiously in patients with porphyria, since these drugs can precipitate an acute attack.  The use of oral sulfonamides is considered contraindicated in patients with porphyria.', '3', '', 'DDInter', 0);
